PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE TABLE IF NOT EXISTS "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE TABLE IF NOT EXISTS "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "personal_access_tokens" ("id" integer primary key autoincrement not null, "tokenable_type" varchar not null, "tokenable_id" integer not null, "name" text not null, "token" varchar not null, "abilities" text, "last_used_at" datetime, "expires_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "categories" ("id" integer primary key autoincrement not null, "name" varchar not null, "slug" varchar not null, "description" text, "image" varchar, "order" integer not null default '0', "is_active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "tags" ("id" integer primary key autoincrement not null, "name" varchar not null, "color" varchar not null default '#3B82F6', "is_active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "contact_messages" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "subject" varchar, "message" text not null, "is_read" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "featured_sections" ("id" integer primary key autoincrement not null, "title" varchar not null, "description" text, "image" varchar, "icon" varchar, "link" varchar, "is_active" tinyint(1) not null default '1', "order" integer not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "home_sliders" ("id" integer primary key autoincrement not null, "title" varchar not null, "subtitle" varchar, "button_text" varchar, "button_link" varchar, "image" varchar not null, "is_active" tinyint(1) not null default '1', "order" integer not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "pages" ("id" integer primary key autoincrement not null, "title" varchar not null, "slug" varchar not null, "content" text, "meta_title" varchar, "meta_description" text, "is_active" tinyint(1) not null default '1', "order" integer not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "products" ("id" integer primary key autoincrement not null, "name" varchar not null, "slug" varchar not null, "description" text, "images" text, "category_id" integer, "features" text, "meta_title" varchar, "meta_description" text, "is_active" tinyint(1) not null default '1', "is_featured" tinyint(1) not null default '0', "order" integer not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("category_id") references "categories"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "product_tag" ("product_id" integer not null, "tag_id" integer not null, foreign key("product_id") references "products"("id") on delete cascade, foreign key("tag_id") references "tags"("id") on delete cascade, primary key ("product_id", "tag_id"));
CREATE TABLE IF NOT EXISTS "settings" ("id" integer primary key autoincrement not null, "logo" varchar, "favicon" varchar, "phone" varchar, "email" varchar, "address" text, "facebook" varchar, "instagram" varchar, "twitter" varchar, "linkedin" varchar, "footer_text" text, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "featured_products" ("id" integer primary key autoincrement not null, "category_label" varchar, "title" varchar not null, "description" text not null, "image" varchar not null, "tags" text, "button_text" varchar not null default 'Detayları Gör', "button_link" varchar not null, "order" integer not null default '0', "is_active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "wholesale_orders" ("id" integer primary key autoincrement not null, "company_name" varchar not null, "contact_person" varchar not null, "email" varchar not null, "phone" varchar not null, "address" text, "city" varchar, "tax_office" varchar, "tax_number" varchar, "items" text not null, "additional_notes" text, "status" varchar check ("status" in ('pending', 'reviewing', 'approved', 'rejected', 'completed')) not null default 'pending', "admin_notes" text, "reviewed_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE INDEX "sessions_user_id_index" on "sessions" ("user_id");
CREATE INDEX "sessions_last_activity_index" on "sessions" ("last_activity");
CREATE INDEX "jobs_queue_index" on "jobs" ("queue");
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE INDEX "personal_access_tokens_tokenable_type_tokenable_id_index" on "personal_access_tokens" ("tokenable_type", "tokenable_id");
CREATE UNIQUE INDEX "personal_access_tokens_token_unique" on "personal_access_tokens" ("token");
CREATE INDEX "personal_access_tokens_expires_at_index" on "personal_access_tokens" ("expires_at");
CREATE UNIQUE INDEX "categories_slug_unique" on "categories" ("slug");
CREATE UNIQUE INDEX "pages_slug_unique" on "pages" ("slug");
CREATE UNIQUE INDEX "products_slug_unique" on "products" ("slug");
COMMIT;
